import React, { Component } from 'react';


function saveCart(cart){
  localStorage.setItem('cart' , JSON.Stringift(cart))
}

function getCart(){
  return JSON.parse(localStorage.getItem('cart'))
}




var item_qte=1;
function handleIncrement (){
  item_qte+1
}
let cart = getCart()
var showCart ='';
showCart = cart.map((item) => (
  <tr>
  <td className="car_image">
    <p></p>

  </td>
  <td className="cart_price">
    <p>{item.product_desc}</p>
  </td>
  <td>
<div>
  <button type='button' onClick={()=>handleIncrement(item_qte)} className='input-group-text'>+</button>
  <p>{item_qte}</p>
  <button type='button' onClick={()=>handleDeccrement(item_qte)} className='input-group-text'>-</button>
</div>
  </td>
  <td className="cart_total">
    <p className="cart_total_price">{item.product_price}</p>
  </td>
 <td><p className="cart_total_price">{item.product_price*item_qte}</p></td>
</tr>

))

function Panier() {

  return (<table className="table table-bordered">
  <thead>
    <tr>
      <th>Product</th>
      <th>Description</th>
      <th>le nombre_de_produit_commander</th>
      <th>Price</th>


      <th>Total</th>
    </tr>
  </thead>
  <tbody>


  {showCart}



  </tbody>
</table>
  )
}

export default Panier;
