import Categorie from './Categorie';
import React, { useState } from 'react';
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux'
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

import Button from '@mui/material/Button';
import { Link } from 'react-router-dom';
// import { LOGIN_SUCCESS,GET_TICKETS_SUCCESS, REGISTER_SUCCESS } from '../types';
import {register} from '../redux/actions/user.actions';

function Sinscrire() {
  const [nom, setnom] = useState("");
  const [password, setpassword] = useState("");
  const [pseudo, setpseudo] = useState("");
  const [prenom, setprenom] = useState("");
  const [cin, setcin] = useState("");
  const [adresse, setadresse] = useState("");
  const [telephone, settelephone] = useState("");
  const [codePostal, setcodePostal] = useState("");
  const [sexe, setsexe] = useState("");
  const [email, setemail] = useState("");

  const handlenom = (e) => {
    setnom(e.target.value)
  }

  const handleemail = (e) => {
    setemail(e.target.value)
  }

  const handlepassword = (e) => {
    setpassword(e.target.value)
  }

  const handlepseudo = (e) => {
    setpseudo(e.target.value)
  }

  const handleprenom = (e) => {
    setprenom(e.target.value)
  }

  const handlecin = (e) => {
    setcin(e.target.value)
  }

  const handleadresse = (e) => {
    setadresse(e.target.value)
  }

  const handletelephone = (e) => {
    settelephone(e.target.value)
  }

  const handlecodePostal = (e) => {
    setcodePostal(e.target.value)
  }

  const handlesexe = (e) => {
    setsexe(e.target.value)
  }


  const dispatch = useDispatch();
  const history = useHistory();

  const handleSubmit = async () => {

    let data = {
      nom : nom,
      prenom :prenom ,
      password : password,
      email : email,
      pseudo :pseudo ,
      sexe : sexe,
      codePostal : codePostal,
      cin : cin,
      telephone : telephone,
      adresse  : adresse
    }
    dispatch(await register(data, history))
  }



  return (        <div className='register'>

  <React.Fragment>
      <Container>
          <Box
              component="form"
              sx={{
                  '& > :not(style)': { m: 1, width: '25ch' },
              }}
              noValidate
              autoComplete="off"
          >

              <h1>Register to Envast Support</h1>
              <TextField


                  style={{ width: '60%', borderRadius: '15px' }} id="outlined-basic" label="Name" variant="outlined"
              />

              <TextField


                  style={{ width: '60%', borderRadius: '15px' }} id="outlined-basic" label="Email" variant="outlined" />

              <TextField


                  type="password"
                  style={{ width: '60%', borderRadius: '15px' }} id="outlined-basic" label="password" variant="outlined" />
              <TextField

                  type="password"
                  style={{ width: '60%', borderRadius: '15px' }} id="outlined-basic" label="confirmPassword" variant="outlined" />
              <br />
              <Button
                  onClick={handleSubmit}
                  style={{ height: "55px" }} variant="contained" endIcon={<SendIcon />}>
                  Create
              </Button>
              <Link to={'/login'}>Already a membre !</Link>

          </Box>
      </Container>
  </React.Fragment>
</div>);
}


export default Sinscrire;
