import React, { Component } from 'react';


function saveCart(cart){
  localStorage.setItem('cart' , JSON.Stringift(cart))
}

function getCart(){
  return JSON.parse(localStorage.getItem('cart'))
}




 var A =1;
function handleIncrement  ()  {
  console.log(A)
   A++ ;
}


function handleDeccrement (){
  return A-- ;
}


let cart = getCart()
var showCart ='';
showCart = cart.map((item) => (
  <tr>
  <td className="car_image">
    <p></p>

  </td>
  <td className="cart_price">
    <p>{item.product_desc}</p>
  </td>
  <td>
<div>
  <button type='button' onClick={()=>handleIncrement()} className='input-group-text'>+</button>
  <div>{A}</div>
  <button type='button' onClick={()=>handleDeccrement()} className='input-group-text'>-</button>
</div>
  </td>
  <td className="cart_total">
    <p className="cart_total_price">{item.product_price}</p>
  </td>
 <td><p className="cart_total_price">{item.product_price*A}</p></td>
</tr>

))

function Panier() {

  return (<table className="table table-bordered">
  <thead>
    <tr>
      <th>Product</th>
      <th>Description</th>
      <th>le nombre_de_produit_commander</th>
      <th>Price</th>


      <th>Total</th>
    </tr>
  </thead>
  <tbody>


  {showCart}



  </tbody>
</table>
  )
}

export default Panier;
