  //     console.log(res)
  //   }).catch(err => {
  //     console.log(err)
  //   })
  // }






   
        return (<div> <Categorie /><div className="col-sm-9 padding-right"> <div>
            <div>
        <div className="review-payment">
          <h2>Intervention</h2>
        </div>
        <div className="table-responsive cart_info">
          <table className="table table-condensed">
            <thead>
              <tr className="cart_menu">
                <td className="image">PC name</td>
               
                <td className="price">Prix</td>
                <td className="total">Etat</td>
                <td />
              </tr>
            </thead>
            <tbody>
            {Intervention.map((a) => (
              <tr>
                <td className="car_image">
                  <h4>a.name</h4>
                 
                </td>
                <td className="cart_price">
                  <p>a.Prix</p>
                </td>
                <td className="cart_total">
                  <p className="cart_total_price">mezel</p>
                </td>
              </tr>
                
                ))}
            </tbody>
           
          </table>
           
        </div>
      </div>
        </div> </div></div>);
   
}
 
export default List;
